 //api fetch goes here
 //Note: template for actions is 'controller/action/id?'
 const fetchMe = async () => {
  const response = await fetch("https://localhost:7010/Owner/aboutme");
  let myData;
  try {
    myData = await response.json();
    setData(myData);
  } catch(err) {
    alert(err);
  }
  console.log(myData);
  }
  fetchMe();
  setMeDisp(Object.keys(data));
  },[]);
  //id, name, skills, likes, contact
  return (
    <div id="content">
      <h1>About Me</h1>
      <div>
        <ul>{
          meDisp.slice(1,).map((item, index)=>
          <li id={'cues'+index} key={index}  className="tag" onClick={()=>changeProp(index, item)}>{item}</li>
          )
        }</ul>
      </div>
  </div>
  );
}
export default App;
